#!/usr/bin/env python3

import argparse, base64, os

# DO NOT MODIFY, USE ./updateBoostraper.py
files = {'Makefile.am': b'', 'apertium-{{languageCode}}.{{languageCode}}.dix': b'', 'AUTHORS': b'', 'apertium-{{languageCode}}.pc.in': b'', 'configure.ac': b'', 'apertium-{{languageCode}}.{{languageCode}}.rlx': b'', 'apertium-{{languageCode}}.{{languageCode}}.acx': b'PD94bWwgdmVyc2lvbj0iMS4wIj8+CjxhbmFseXNpcy1jaGFycz4KICA8IS0tIERlc2NyaWJlIGVxdWl2YWxlbnQgY2hhcmFjdGVycyBpbiBtb25vZGljZXMgaGVyZS4gU2VlIGh0dHA6Ly93aWtpLmFwZXJ0aXVtLm9yZy93aWtpL0FjeCBmb3IgbW9yZSBpbmZvcm1hdGlvbiAtLT4KPC9hbmFseXNpcy1jaGFycz4K', 'modes.xml': b'', 'autogen.sh': b'', 'COPYING': b'', 'README': b''}
# DO NOT MODIFY, USE ./updateBoostraper.py

def makeReplacements(input, replacements):
    for replacementName, replacementValue in replacements.items():
        input = input.replace('{{%s}}' % replacementName, replacementValue)
    return input

if __name__ == '__main__':
    parser = argparse.ArgumentParser(description='Bootstrap an Apertium lttoolbox-based language module')
    parser.add_argument('languageCode', help='ISO-639-3 language code')
    parser.add_argument('languageName', help='Preferred name of language')
    parser.add_argument('-d', '--destination', help='Destination directory for new language module', default=os.getcwd())
    args = parser.parse_args()

    replacements = {'languageCode': args.languageCode, 'languageName': args.languageName}
    
    for filename, encodedFile in files.items():
        with open(os.path.join(args.destination, makeReplacements(filename, replacements)), 'w', encoding='utf-8', newline='\n') as f:
            f.write(makeReplacements(str(base64.b64decode(encodedFile), encoding='utf-8'), replacements))
